import React from 'react'
import Add from '../images/plus.svg'
import Complete from '../images/chevron-down.svg'
// import CheckFull from '../images/check-circle-fill.svg'
import Check from '../images/check-circle.svg'
export default function Main({Addtask,todo}) {
  const [formData,setFormData] =React.useState("")
  function handleChange(e){
    setFormData(e.target.value)
  }
  // function handleSubmit(e){
  //   e.preventDefault()
  //   addtask(formData)
  // }
  return (
    <div className="main">
        <div className="main-title">
            <h1>My Day</h1>
        </div>
        <div className="main-content">
            <span className="complete"><img src={Complete}/><h3>Completed</h3></span>
            <div className="list">
              <span className="list-items"><img src={Check}/><h4>{todo.tas}</h4></span>
            </div>
        </div>
        <form className="form" onSubmit={(event)=>{
          event.preventDefault();
          Addtask(formData)}}>
           <img src={Add}/>
            <input type="text" placeholder="Add new task " onChange={handleChange}/>
        </form>
        <div>
        
        </div>
    </div>
  
  )
}
