import React from 'react'
import Add from '../images/plus.svg'
import Complete from '../images/chevron-down.svg'
import CheckFull from '../images/check-circle-fill.svg'
import Check from '../images/check-circle.svg'
export default function Main(props) {
  const [formData,setFormData]=React.useState({
    task:""
})
  function handleChange(event){
    setFormData(formData=>{
      return {
        ...formData,[event.target.name]:event.target.value
      }
    })
  }
  function handleSubmit(event){
    e.preventDefault()
  }
  console.log(formData)
  return (
    <div className="main">
        <div className="main-title">
            <h1>My Day</h1>
        </div>
        <div className="main-content">
            <span className="complete"><img src={Complete}/><h3>Completed</h3></span>
            <div className="list">
              <span className="list-items"><img src={Check}/><h4>{props.data[0].task}</h4></span>
            </div>
        </div>
        <form className="form" onSubmit={handleSubmit}>
           <img src={Add}/>
            <input type="text" placeholder="Add new task " onChange={handleChange}name="task" value={formData.task}/>
        </form>
        <div>
        
        </div>
    </div>
  
  )
}
